package uk.org.winder.maths.factorial

import java.math.BigInteger

import io.kotlintest.specs.StringSpec
import io.kotlintest.KTestJUnitRunner

import org.junit.runner.RunWith

@RunWith(KTestJUnitRunner::class)
class Factorial_KotlinTest_ExampleBased : StringSpec() {
  init {

    // Cannot use ::iterative, ::recursive, ::tail_recursive, ::reductive here as each is an overloaded
    // function and there is (as at 2015-12-22) no way of disambiguating.
    // So create lambdas, to force correct types and hence function selection

    val algorithms = listOf(
        "iterative" to {x:Int -> iterative(x)},
        "reductive" to {x:Int -> reductive(x)},
        "na誰ve_recursive" to {x:Int -> na誰ve_recursive(x)},
        "tail_recursive" to {x:Int -> tail_recursive(x)}
    )

    val positiveData = listOf(
        0 to BigInteger.ONE,
        1 to BigInteger.ONE,
        2 to  2.bigint,
        3 to 6.bigint,
        4 to 24.bigint,
        5 to 120.bigint,
        6 to 720.bigint,
        7 to 5040.bigint,
        8 to 40320.bigint,
        9 to 362880.bigint,
        10 to 3628800.bigint,
        11 to 39916800.bigint,
        12 to 479001600.bigint,
        13 to 6227020800.bigint,
        14 to 87178291200.bigint,
        20 to 2432902008176640000.bigint,
        30 to BigInteger("265252859812191058636308480000000"),
        40 to BigInteger("815915283247897734345611269596115894272000000000"),
        1000 to BigInteger(""),
        2000 to BigInteger("")
    )

    val negativeData = listOf(-1, -2, -5, -10, -20, -100)

    forAll(algorithms) {a ->

      forAll(positiveData) {p ->
        (a.first + "(" + p.first + ") == " + p.second) { a.second(p.first) shouldEqual p.second }
      }

      forAll(negativeData) {n ->
        (a.first + "(" + n + ") throws IllegalArgumentException") {
          shouldThrow<IllegalArgumentException> { a.second(n) }
        }
      }

    }

    "iterative of a huge number succeeds" { iterative(26000) }

    "reductive of a huge number succeeds" { reductive(26000) }

    "na誰ve_recursive of a huge number fails with a stack overflow" {
      shouldThrow<StackOverflowError> { na誰ve_recursive(26000) }
    }

    "tail recursive of a huge number succeeds" { tail_recursive(26000) }

  }
}
